mapper.select:
select count(*) from dept d,patrol_area pa where d.deptId=pa.deptId

	and pa.name like CONCAT('%',#{name},'%')


	and d.deptId=#{deptId}


	and d.deptName like CONCAT('%',#{deptName},'%')


	and pa.createTime between #{createTimeStart} and #{createTimeEnd}

select pa.*,d.deptName from dept d,patrol_area pa where d.deptId=pa.deptId

	and pa.name like CONCAT('%',#{name},'%')


	and d.deptId=#{deptId}


	and d.deptName like CONCAT('%',#{deptName},'%')


	and pa.createTime between #{createTimeStart} and #{createTimeEnd}

limit #{rowNum},#{rows}
