mapper.select:
select count(*) from patrol_device_account pda,patrol_device pd,dept d where 1=1 and pda.pdId=pd.id and pd.deptId=d.deptId

	and pda.no like CONCAT('%',#{no},'%')


	and pd.name like CONCAT('%',#{pdName},'%')


	and d.deptId=#{deptId}


	and d.name like CONCAT('%',#{deptName},'%')


	and pda.createTime between #{createTimeStart} and #{createTimeEnd}


	and pda.startTime between #{startTimeStart} and #{startTimeEnd}

select pda.*,pd.name pdName,d.deptName from patrol_device_account pda,patrol_device pd,dept d where 1=1 and pda.pdId=pd.id and pd.deptId=d.deptId

	and pda.no like CONCAT('%',#{no},'%')


	and pd.name like CONCAT('%',#{pdName},'%')


	and d.deptId=#{deptId}


	and d.deptName like CONCAT('%',#{deptName},'%')


	and pda.createTime between #{createTimeStart} and #{createTimeEnd}


	and pda.startTime between #{startTimeStart} and #{startTimeEnd}

order by pda.createTime desc
limit #{rowNum},#{rows}

select pda.id,pda.no from patrol_device_account pda,patrol_device pd where pda.pdId=pd.id

	and pd.id=#{deptId}


	and pda.pdId=#{pdId}

 order by createTime
