mapper.select:
select count(*) from patrol_device_account pda,patrol_device pd,patrol_device_type pdt where 1=1 and pda.pdId=pd.id and pd.pdtId=pdt.id

	and pda.no like CONCAT('%',#{no},'%')


	and pd.name like CONCAT('%',#{pdName},'%')


	and pdt.name like CONCAT('%',#{pdtName},'%')


	and pda.createTime between #{createTimeStart} and #{createTimeEnd}


	and pda.startTime between #{startTimeStart} and #{startTimeEnd}

select pda.*,pd.name pdName,pdt.name pdtName from patrol_device_account pda,patrol_device pd,patrol_device_type pdt where 1=1 and pda.pdId=pd.id and pd.pdtId=pdt.id

	and pda.no like CONCAT('%',#{no},'%')


	and pd.name like CONCAT('%',#{pdName},'%')


	and pdt.name like CONCAT('%',#{pdtName},'%')


	and pda.createTime between #{createTimeStart} and #{createTimeEnd}


	and pda.startTime between #{startTimeStart} and #{startTimeEnd}

order by pda.createTime desc
limit #{rowNum},#{rows}
