mapper.select:
select count(*) from patrol_line where 1=1

	and name like CONCAT('%',#{name},'%')


	and createTime between #{createTimeStart} and #{createTimeEnd}

select `name`,createTime from patrol_line where 1=1

	and name like CONCAT('%',#{name},'%')


	and createTime between #{createTimeStart} and #{createTimeEnd}

order by createTime desc
limit #{rowNum},#{rows}
